import React from "react";

const Footer = ({ sharedBasicInfo }) => {
    let networks;
    let contactDetails;
    if (sharedBasicInfo) {
        networks = sharedBasicInfo.social.map((network) => (
            <span key={network.name} className="m-4">
                <a href={network.url} alt={network.description} target="_blank" rel="noopener noreferrer">
                    <i className={network.class}></i>
                </a>
            </span>
        ));
        contactDetails = sharedBasicInfo.contact.map((info) => (
            <span key={info.city} className="m-4">
                <span>{info.region}</span>
            </span>
        ));
    }

    return (
        <footer>
            <div className="col-md-12">
                <div className="contact-info">

                </div>
                <div className="social-links">{networks}</div>
                <div className="copyright py-4 text-center">
                    <div className="container">
                        <small>
                            Copyright &copy;{" "}
                            {sharedBasicInfo ? sharedBasicInfo.name : "???"}
                        </small>
                    </div>
                </div>
            </div>
        </footer>
    );
}

export default Footer;
